document.addEventListener("DOMContentLoaded",()=>{var e=document.querySelectorAll(".changeSection");const n=document.querySelectorAll(".contentSection"),o=document.getElementById("background_hero");e.forEach(t=>{t.addEventListener("click",e=>{e.preventDefault(),n.forEach(e=>{e.style.display="none"}),o.style.display="none",document.getElementById(t.getAttribute("data-target")).style.display="block"})})});